package com.limn.log;import java.net.Socket;/** * 日志记录的控制器 * @author limn * */public class RunLog{			private static LogControlInterface runLog = null;				public RunLog(){		runLog = new RunLogDriver();	}		public RunLog(Socket sc){		runLog = new RunLogDriver(sc);	}		public RunLog(PrintLogDriver PLD){		runLog = PLD;	}		public static boolean isStart(){		return runLog==null?false:runLog.isStart();	}		public static void init(int count){		if(runLog instanceof RunLogDriver){			((RunLogDriver)runLog).progressLength(count);		}	}		public static void printLog(String log,int style){		runLog.printLog(log, style);	}		public static void printLocalLog(String log,int style){		runLog.printLocalLog(log, style);	}		public static void debug(String log){		runLog.printLog(log,4);	}		public static void info(String log,int style){		runLog.printLog(log,style);	}		public static void error(String log){		runLog.printLog(log,2);	}	public static void highLightCurrentStep(int currentNum){		if(runLog instanceof RunLogDriver){			((RunLogDriver)runLog).highLightCurrentStep(currentNum);		}	}		public static void setStepsForTextAreaByIndex(int currentStepNum, String[] currentStep){		if(runLog instanceof RunLogDriver){			((RunLogDriver)runLog).setStepsForTextAreaByIndex(currentStepNum,currentStep);		}	}			}